// Sticky navbar on scroll
window.addEventListener('scroll', function() {
    const navbar = document.getElementById('navbar');
    if (window.scrollY > 50) {
        navbar.classList.add('sticky');
    } else {
        navbar.classList.remove('sticky');
    }
});

// Close dropdowns when clicking outside on mobile
document.addEventListener('click', function(event) {
    const navbarContent = document.getElementById('navbarContent');
    const navbarToggler = document.querySelector('.navbar-toggler');
    
    if (window.innerWidth <= 991 && navbarContent.classList.contains('show')) {
        if (!navbarContent.contains(event.target) && !navbarToggler.contains(event.target)) {
            const bsCollapse = new bootstrap.Collapse(navbarContent);
            bsCollapse.hide();
        }
    }
});

// Close mobile menu when clicking a link (except dropdown toggles)
document.querySelectorAll('.nav-link:not(.dropdown-toggle), .dropdown-item, .mega-link').forEach(link => {
    link.addEventListener('click', function() {
        if (window.innerWidth <= 991) {
            const navbarContent = document.getElementById('navbarContent');
            if (navbarContent.classList.contains('show')) {
                const bsCollapse = new bootstrap.Collapse(navbarContent);
                bsCollapse.hide();
            }
        }
    });
});

// Prevent dropdown from closing when clicking inside mega dropdown on desktop
document.querySelectorAll('.mega-dropdown, .dropdown-menu').forEach(dropdown => {
    dropdown.addEventListener('click', function(e) {
        if (window.innerWidth > 991) {
            e.stopPropagation();
        }
    });
});
// Initialize carousels with auto-slide
document.addEventListener('DOMContentLoaded', function() {
    // Jobs Carousel
    const jobsCarousel = new bootstrap.Carousel(document.getElementById('jobsSlider'), {
        interval: 5000,
        wrap: true
    });
    
    // Projects Carousel
    const projectsCarousel = new bootstrap.Carousel(document.getElementById('projectsSlider'), {
        interval: 5500,
        wrap: true
    });
    
    // Ideas Carousel
    const ideasCarousel = new bootstrap.Carousel(document.getElementById('ideasSlider'), {
        interval: 6000,
        wrap: true
    });
    
    // Pause on hover
    const carousels = document.querySelectorAll('.carousel');
    carousels.forEach(carousel => {
        carousel.addEventListener('mouseenter', () => {
            const bsCarousel = bootstrap.Carousel.getInstance(carousel);
            bsCarousel.pause();
        });
        
        carousel.addEventListener('mouseleave', () => {
            const bsCarousel = bootstrap.Carousel.getInstance(carousel);
            bsCarousel.cycle();
        });
    });
});